param($target)
if(-not $target){Write-Host "Usage: sqlmap.ps1 -target <url>";exit}
$out="reports\sqlmap_ps\" + (Get-Date -Format "yyyyMMdd_HHmmss")
New-Item -ItemType Directory -Path $out -Force
sqlmap -u $target --batch --output-dir=$out
